package sample;import javafx.application.Platform;import javafx.fxml.FXML;import javafx.fxml.FXMLLoader;import javafx.scene.Parent;import javafx.scene.Scene;import javafx.scene.control.*;import javafx.stage.Stage;import java.io.IOException;import javafx.event.ActionEvent;public class Controller {    private boolean userIsInTheMiddleOfInput = false;    private final CalculatorBrain Brain = new CalculatorBrain();    @FXML private MenuBar bar;    @FXML  private Label display;    @FXML    private void digitWasPressed (ActionEvent actionEvent) {        String digit = ((Button) actionEvent.getSource()).getText();        if (userIsInTheMiddleOfInput) {            String textCurrentlyInDisplay = display.getText();            display.setText(textCurrentlyInDisplay + digit);        } else {            display.setText(digit);            userIsInTheMiddleOfInput = true;        }    }    private Double getDisplayValue() {        return new Double(display.getText());    }    private void setDisplayValue(Double displayValue) {        display.setText(String.valueOf(displayValue));    }    private void setDisplayValue(Integer displayValue) {        display.setText(String.valueOf(displayValue));    }    @FXML    private void performOperation(javafx.event.ActionEvent actionEvent) {        String operation = ((Button) actionEvent.getSource()).getText();        if (userIsInTheMiddleOfInput) {            Brain.setOperand(getDisplayValue());            Brain.setOperand(getDisplayValue());            userIsInTheMiddleOfInput = false;        }        Brain.performOperation(operation);        if (Brain.getResult() != null) {            if (Brain.getResult() % 1.0 == 0.0) {                setDisplayValue((Brain.getResult()).intValue());            } else {                setDisplayValue(Brain.getResult());            }        }    }    @FXML    private void closeApplication() {        Platform.exit();    }    @FXML    private void openFile() throws IOException {        try {            Brain.readFromFile();        } catch (IOException e) {            e.printStackTrace();        }    }    @FXML    private void changeToScientific() throws IOException {        Parent scientificViewParent = FXMLLoader.load(getClass().getResource("Scientific.fxml"));        Scene scientificViewScene = new Scene(scientificViewParent);        Stage window = (Stage) (bar).getScene().getWindow();        window.setTitle("Scientific");        window.setScene(scientificViewScene);        window.show();    }}